CREATE TABLE stores (id INT AUTO_INCREMENT NOT NULL, name TEXT NOT NULL COLLATE utf8_unicode_ci, access_token TEXT NOT NULL COLLATE utf8_unicode_ci, authorized_date DATETIME DEFAULT CURRENT_TIMESTAMP NOT NULL, valid TINYINT(1) DEFAULT '1' NOT NULL, invalidated_date DATETIME DEFAULT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;