CREATE TABLE funnel_forms (id INT AUTO_INCREMENT NOT NULL, type INT NOT NULL, name VARCHAR(255) NOT NULL COLLATE utf8_unicode_ci, script_uri VARCHAR(255) DEFAULT NULL COLLATE utf8_unicode_ci, script_loaded TINYINT(1) DEFAULT '0', purchase_button_label VARCHAR(255) DEFAULT NULL COLLATE utf8_unicode_ci, show_variants_button_label VARCHAR(255) DEFAULT NULL COLLATE utf8_unicode_ci, hide_variants_button_label VARCHAR(255) DEFAULT NULL COLLATE utf8_unicode_ci, show_order_total TINYINT(1) DEFAULT '0', reload_on_product_change TINYINT(1) DEFAULT '0', reloaded_at DATETIME DEFAULT NULL, created_at DATETIME DEFAULT CURRENT_TIMESTAMP, updated_at DATETIME DEFAULT CURRENT_TIMESTAMP, INDEX idx_funnel_forms_type (type), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
ALTER TABLE funnel_forms ADD CONSTRAINT fk_funnel_forms_type FOREIGN KEY (type) REFERENCES funnel_form_types (id) ON UPDATE NO ACTION ON DELETE NO ACTION;